<?php
session_start();
$usuario = isset($_SESSION['usuario']) ? $_SESSION['usuario'] : 'Invitado';

require 'conexion.php';

try {
    $stmt = $pdo->query("SELECT id, usuario, nombre, apellidos, rol FROM usuarios");
    $usuarios = $stmt->fetchAll(PDO::FETCH_ASSOC);
    //var_dump($usuarios);

    // Consulta de roles    
    $sql_roles = "SELECT id_rol, descrip FROM roles";
    $result_roles = $pdo->query($sql_roles);
    $roles = $result_roles->fetchAll(PDO::FETCH_ASSOC);
    //var_dump($roles);
    // Consulta de roles    
    $sql_username = "SELECT nombre, apellidos FROM usuarios WHERE usuario = :usuario";
    $stmt = $pdo->prepare($sql_username);
    $stmt->execute([':usuario' => $usuario]);
    $username = $stmt->fetchAll(PDO::FETCH_ASSOC);
    //var_dump($username);    
} catch (PDOException $e) {
    echo "Error al consultar usuarios: " . $e->getMessage();
}

